import React from "react";
import MealCarousel from './carousel';
import MealCategory from './mealCategory';

const home=(props)=>{
    return(
        <div className={"greenBackground"}>
            <MealCarousel/>
            <div className={"container mt-5 pb-5 text-center"}>
                <span className={"title"}>Meal categories</span>
                <div id={"homeMealCategory"} className={"row"}>
                    {props.mealCategories.map((term)=>{
                        return(
                            <MealCategory  category={term} onFilterByCategory={props.onFilterByCategory} />
                        )
                    })}
                </div>
            </div>
        </div>
    )
}

export default home;

import Carousel from 'react-bootstrap/Carousel'
import React, {Component} from "react";
import firstImage from '../../images/first.png';
import secondImage from '../../images/second.jpg';
import  forthImage from '../../images/forth.jpg';


class MealCarousel extends Component{
    render() {
        return(
            <Carousel>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={forthImage}
                        alt="Second slide"
                    />

                    <Carousel.Caption className={"centerCaption"}>
                        <h1 className={"fontSize"}>Order - Pay - Eat</h1>
                        <a className={"btn btn-lg btn-light btn-block fontSize p-3"} href={"/menu"}>VIEW MENU</a>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={secondImage}
                        alt="First slide"
                    />
                    <Carousel.Caption className={"description"}>
                        <h1 className={"pb-4"}>Always fresh food and fast delivery</h1>
                        <h3>
                            <span className={"border rounded p-2"}>45 min.</span>
                            <span className={"ps-2"}>Delivery time</span>
                        </h3>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100"
                        src={firstImage}
                        alt="Third slide"
                    />

                </Carousel.Item>
            </Carousel>
        )
    }
}

export default MealCarousel;

import React from "react";

const mealCategory=(props)=>{
    return(
        <div className={"col-md-4 pt-4 "} key={props.category.id}>
            <div className={"border-0 gridImageOnHover"}>
                <a className={"btn mealCategory-"+props.category.id} onClick={()=>props.onFilterByCategory(props.category.id)}
                      href={`/menu/mealCategory/${props.category.id}`}>
                    <img src={props.category.imageUrl} className="card-img-top gridImages" alt="mealCategoryImage"/>
                    <div className="card-body">
                        <h2 className={"goldText"}>
                            {props.category.name} ({props.category.numberOfMeals})
                        </h2>
                    </div>
                </a>
            </div>
