}

export default mealCategory;

import React from "react";
import {useHistory} from 'react-router-dom';


const Login=(props)=>{

    const history = useHistory();

    const [formData, updateFormData] = React.useState({
        username: "",
        password:""
    })


    const handleChange=(e)=>{
        updateFormData({
            ...formData,
            [e.target.name] : e.target.value.trim()
        })
    }

    const onFormSubmit=(e)=>{
        e.preventDefault();

        const username=formData.username;
        const password=formData.password;

        props.onLoginUser(username,password);

        setTimeout(()=>{
            history.push("/menu");
        },2000)
    }
    /* istanbul ignore next */
    (function () {

        // Fetch all the forms we want to apply custom Bootstrap validation styles to
        var forms = document.querySelectorAll('.needs-validation')

        // Loop over them and prevent submission
        Array.prototype.slice.call(forms)
            .forEach(function (form) {
                form.addEventListener('submit', function (event) {
                    if (!form.checkValidity()) {
                        event.preventDefault()
                        event.stopPropagation()
                    }

                    form.classList.add('was-validated')
                }, false)
            })
    })()
    return(
        <div id={"loginForm"} className={"container mt-5 mb-5"}>
            <div className={"centeredForms"}>
                <div className={"ps-3 pe-3"}>
                    <h1 className={"title text-center"}>Login</h1>
                    <hr/>
                    { localStorage.getItem("loginError") === "yes" &&
                        <div className="alert alert-danger" id="invalidData" role="alert">
                            Invalid username or password.
                        </div>
                    }
                    <form id={"loginData"} className="row g-3 needs-validation" noValidate onSubmit={onFormSubmit}>
                        <div className="col-md-12">
                            <label htmlFor="username" className="form-label">Email</label>
                            <div className="input-group has-validation">
                                <span className="input-group-text" id="inputGroupEmail"><i className="fa fa-envelope"/></span>
                                <input type="text" className="form-control" id="username" name="username"
                                       aria-describedby="inputGroupEmail" placeholder="someone@example.com"
                                       onChange={handleChange}
                                       required/>
                                <div className="invalid-feedback" id={"noUsername"}>
                                    Please enter your username.
                                </div>
                            </div>
                        </div>

                        <div className="col-md-12">
                            <label htmlFor="password" className="form-label">Password</label>
                            <div className="input-group has-validation">
                            <span className="input-group-text" id="inputGroupPassword"><i className="fa fa-lock"
                                                                                          aria-hidden="true"/></span>
                                <input type="text" className="form-control" id="password" name="password"
                                       aria-describedby="inputGroupPassword" placeholder="Your password"
                                       onChange={handleChange}
                                       required/>
                                <div className="invalid-feedback" id={"noPassword"}>
                                    Please enter you password.
                                </div>

                            </div>
                        </div>

                        <div className={"d-grid gap-2 col-md-12 mt-4"}>
                            <button id="loginSubmit" type="submit" className="btn btn-block" style={{backgroundColor: '#004332'}}>
                                <span className={"goldText"}>Login</span>
                            </button>
                        </div>


                    </form>
                    <span className={"text-muted d-flex justify-content-center mt-2"}>Don't have an account?
                        <a href={"/register"}>Register now</a>
                    </span>
                </div>
            </div>
        </div>
)

}

export default Login;

import React from "react";
import {Link, Redirect} from "react-router-dom";

const MealCategory=(props)=>{

    if (localStorage.getItem("userRole")===null ||  !localStorage.getItem("userRole").endsWith("ADMIN"))
    {
        //console.log(localStorage.getItem("userRole") !== "ROLE_ADMIN")
        return (
            <Redirect to={"/unauthorized"} />
        )
    }
    return(
        <div className={"container mt-5 pb-5 text-center"}>
            <span className={"title"}>Meal categories</span>
            <div className="col mt-3 mb-3">
                <div className="row">
                    <div className="d-grid gap-2 col-sm-12">
                        <Link className={"btn btn-block"} id={"addMealCategory"} style={{backgroundColor:'#004332'}} to={"/mealCategories/add"}>
                            <span className={"goldText fw-bold"}>Add new meal category</span>
                        </Link>
                    </div>
                </div>
            </div>
            <div className={"row"}>
                <div className={"table-responsive"}>
                    <table className={"table table-striped"}>
                        <thead>
                        <tr>
                            <th scope={"col-md-3"}>Image</th>
                            <th scope={"col-md-3"}>Name</th>
                            <th scope={"col-md-6"}>Edit/Delete</th>
                        </tr>
                        </thead>
                        <tbody>
                        {props.mealCategories.map((term)=>{
                            return(
                                <tr className={"mealCategoriesRow"}>
                                    <td>
                                        <img className={"imageInTable mealCategoryImage-"+term.id} src={term.imageUrl} alt={"meal category"}/>
                                    </td>
                                    <td className={"text-center align-middle mealCategoryName-"+term.id}>
                                        <h3>{term.name}</h3>
                                    </td>
                                    <td className={"text-right align-middle"}>
                                        <div className={"d-grid gap-2 col-md-12 "}>
                                            <Link className={"btn btn-sm text-white mealCategoryDelete-"+term.id} style={{backgroundColor: '#451F16'}}
                                                  onClick={() => props.onDeleteMealCategory(term.id)}>
                                                Delete
                                            </Link>

                                            <Link className={"btn btn-sm mealCategoryEdit-"+term.id} style={{backgroundColor: '#bb9c57'}}
                                                  onClick={()=>props.onEditMealCategory(term.id)}
                                                  to={`/mealCategories/edit/${term.id}`}>
                                                <span className={"greenText"}>Edit</span>
                                            </Link>
                                        </div>
                                    </td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>

            </div>
        </div>
    )
}

export default MealCategory;

import React from "react";
